	/**
	 *	@description Add model
	 *
	 *	@param {integer} modelId - ID of the model
	 *	@param {requestCallback} [cb] - Optional callback function
	 */
	this.addModel = function(parameters, cb) {
		cb = cb || handleCallback;
		parameters.type = parameters.type || 'STL';
		if (parameters.contents !== undefined) {
			managers.ModelManager.addModelByContents(parameters.contents, { type: parameters.type, resetRoot: parameters.resetRoot }, cb);
		}
		else if (parameters.url !== undefined) {
			managers.ModelManager.addModelByUrl(parameters.url, { type: parameters.type, resetRoot: parameters.resetRoot }, cb);
		}
		else if (parameters.geometry !== undefined) {
			managers.ModelManager.addGeometry(parameters.geometry, { callback: cb, resetRoot: parameters.resetRoot }, cb);
		}
		else {
			cb("Please provide either parameters.contents, parameters.url or parameters.geometry");
		}
	};

